import React from 'react';
import './App.css';
import data from './data';

function App() {
  const openMenu = () => {
    document.querySelector(".sidebar").classList.add("open");
  };
  const closeMenu = () => {
    document.querySelector(".sidebar").classList.remove("open");
  }
  return (
    <div className="grid-container">
      <header className="header">
        <div className="brand">
          <button onClick={openMenu}>
            &#9776;
          </button>       
          <a href="index.hmtl">Dada Laris</a>
        </div>  
        <div className="header-links">
          <a href="cart.html">Cart</a>
          <a href="signin.html">Sign In</a>
        </div>
      </header>
      <aside className="sidebar">
        <h3>Shopping Categories</h3>
        <button className="sidebar-close-button" onClick={closeMenu}>x</button>
        <ul>
          <li>
            <a href="index.html">Summer collection</a>
          </li>
          <li>
            <a href="index.html">Winter collection</a>
          </li>
        </ul>
      </aside>  
      <main className="main">
        <div className="content">
          <ul className="products">
          {
            data.products.map(product => 
             <li>
              <div className="product">
                <img className="product-image" src={product.img} alt="product"/>
                <div className="product-name">
                  <a href="product.html">{product.name}</a>
                </div>
                <div className="product-brand">{product.brand}</div>
                <div className="product-price">&#8364;{product.price}</div>
                <div className="product-rating">{product.rating}</div>
              </div>
            </li> )  
          }                                 
          </ul>
        </div>
      </main>
      <footer className="footer">
        All right reserved
      </footer>   
    </div>
  );
}

export default App;
