import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

import { detailsProduct } from '../redux/product/products.actions';

function ProductScreen(props) {
	const [qty, setQty] = useState(1);
	const productDetails = useSelector(state => state.productDetails);
	const { product, loading, error } = productDetails;
	const dispatch = useDispatch();
	useEffect(()=> {
		dispatch(detailsProduct(props.match.params.id));
		return () => {
			//
		};
	}, []);
	const handleAddToCart = () => {
		props.history.push("/cart/" + props.match.params.id + "?qty=" + qty);
	}
	return (
	<div>
		<div className='back-to-result'>
			<Link to='/'>Back to result</Link>
		</div>
		{
		loading? <div>Loading...</div>:
		error? <div>{error}</div>:
		(
		<div className='details'>
			<div className='details-image'>
				<img src={product.img} alt='product' />
			</div>
			<div className='details-info'>
				<ul>
					<li><h4>{product.name}</h4></li>
					<li>{product.rating} Stars {product.numReviews} Reviews</li>
					<li>Price: <b>&#8364;{product.price}</b></li>
					<li>
					Description: 
						<div>{product.description}</div>
					</li>
				</ul>
			</div>
			<div className='details-action'>
				<ul>
					<li>Price: &#8364;{product.price}</li>
					<li>
						Status: {product.countInStock > 0 ? "In stock": "Out of stock"}
					</li>
					<li>Qty: <select value={qty} onChange={(e) => {setQty(e.target.value)}}>
						{[...Array(product.countInStock).keys()].map(x=> 
							<option key={x+1} value={x+1}>{x+1}</option>
						)}
					</select>
					</li>
					<li>
						{product.countInStock> 0 && 
							<button onClick={handleAddToCart} className='button primary'>Add to cart</button>
						}
					</li>
				</ul>
			</div>
		</div>
		)
		}				
	</div>
	)
}
export default ProductScreen;
